Addon.JavascriptInHead:
function modalOpen(id) {
			console.log(id);

			var myId = '#'+id['id'];
			console.log(myId);

			var myData = $(id).attr("my-data");
			console.log(myData);

			var myIframe = "<iframe src=\""+myData+"\" style=\"border:none;\" height=\"100%\" width=\"100%\"></iframe>";
			console.log(myIframe);
			$(myId).appendTo("body") 
			$(myId).on('shown.bs.modal', function (e) {
			$(myId).find(".modal-body").html(' ');
			$(myId).find(".modal-body").append(myIframe);
			})

			}

